INSERT INTO `wp_posts` (`post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES 
(1, '2021-07-29 12:10:00', '2021-07-29 12:10:00', '<p class=\"article\">O Diretor de Comunicação da ANSEGTV (Associação Nacional de Segurança e Transporte de Valores), Robson Alexandre Barbara, esteve no BAEP (Batalhão de Ações Especiais de Polícia) de Ribeirão Preto, dia 23/7, para apresentar a Associação ao comando da corporação.</p>

                            <figure class=\"pull-left m-2 text-left\">
                                    <img src=\"https://ansegtv.com.br/website/wp-content/uploads/2025/06/equipe-baep.jpg\" width=\"450\" height=\"auto\" alt=\"Equipe BAEP\" class=\" img-fluid\">	
                                    <figcaption></figcaption>
                            </figure>

                            <p class=\"article\">Em conversa com o Tenente Coronel José Wilson da Silva, Major Helder Antônio de Paula e Tenente Wagner Bianchi, Barbara apresentou a estrutura e objetivos da ANSEGTV, bem como suas principais frentes de atuação em âmbito nacional. “Para nós é de extrema importância a constante troca de informações com autoridades para o desenvolvimento do segmento de segurança privada e a defesa da ética e da livre concorrência no mercado”, comenta o diretor Robson Alexandre Barbara.</p>

                            <p class=\"article\">A direção do batalhão destacou a relevância da ANSEGTV para o setor de segurança privada e transporte de valores e colocou a Polícia Militar do interior de São Paulo à disposição para uma atuação ainda mais segura na região.</p>

                            <p class=\"article\">Entregue à população no final de 2019, o BAEP de Ribeirão Preto segue o padrão Rota de atuação e atende 93 municípios da região, beneficiando mais de 3,7 milhões de pessoas.</p>', 'ANSEGTV é apresentada ao BAEP de Ribeirão Perto, interior de São Paulo', 'O Diretor de Comunicação da ANSEGTV (Associação Nacional de Segurança e Transporte de Valores), Robson Alexandre Barbara, esteve no BAEP (Batalhão de Ações Especiais de Polícia) de Ribeirão Preto, dia 23/7, para apresentar a Associação ao comando da corporação.', 'publish', 'closed', 'closed', '', 'ansegtv-e-apresentada-ao-baep-de-ribeirao-preto-interior-de-sao-paulo', '', '', '2021-07-29 12:10:00', '2021-07-29 12:10:00', '', 0, 'PLACEHOLDER_GUID_FOR_ID', 0, 'post', '', 0);
